import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const state = {tools : []}

const actions = {
  addTool({commit}, tool){

    return commit('addTool', tool);
  },
}

const mutations = {
  addTool(state, payload){
    state.tools.push(payload)
  }
}

const store = new Vuex.Store({
  state,
  actions,
  mutations
})

export default store
