//import { resolve } from 'core-js/fn/promise'
import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const state = {tools : []}

const actions = {
  addTool({commit}, tool){
    return new Promise(resolve => {
      setTimeout(() => {
        tool.id = Date.now()
        commit('addTool', tool)
      }, 1000);
    })
  },
}

const mutations = {
  addTool(state, payload){
    state.tools.push(payload)
  }
}

const store = new Vuex.Store({
  state,
  actions,
  mutations
})

export default store
